mapper.select:
SELECT *
FROM tb_student
WHERE student_id = #{studentId}
  and password = #{password};

SELECT *
FROM tb_student
WHERE name LIKE #{name}
  AND student_id LIKE #{studentId}
  AND title LIKE #{title} LIMIT #{startIndex}
    , #{pageSize};

mapper.update:
UPDATE tb_student
SET mentor_id = #{teacherId}
WHERE student_id = #{studentId}

UPDATE tb_student
SET is_selected = 1
WHERE student_id = #{studentId}

